import React, { useState, useEffect } from 'react';
import './Slider.css';

const Slider = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [isAutoPlay, setIsAutoPlay] = useState(true);

  const slides = [
    {
      id: 1,
      company: '999auto',
      logoImage: '/images/logos/999auto.svg',
      category: '–ê–≤—Ç–æ–º–æ–±–∏–ª–∏',
      description: 'üöó 999AUTO.RU - –õ—É—á—à–∏–µ —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç—ã –∏ –ø—Ä–µ–≤–æ—Å—Ö–æ–¥–Ω–æ–µ –∫–∞—á–µ—Å—Ç–≤–æ! –ú–æ—Ç–æ—Ä—ã –µ–≤—Ä–æ–ø–µ–π—Å–∫–æ–≥–æ —É—Ä–æ–≤–Ω—è —Å –±—ã—Å—Ç—Ä–æ–π –¥–æ—Å—Ç–∞–≤–∫–æ–π...',
      date: '02.08.2025',
      image: '/images/999auto.jpg'
    },
    {
      id: 2,
      company: '–°–±–µ—Ä–±–∞–Ω–∫',
      logoImage: '/images/logos/sberbank.png',
      category: '–ë–∞–Ω–∫–∏', 
      description: 'üî• –ù–û–í–ê–Ø –ö–ê–†–¢–ê –°–ë–ï–†–ë–ê–ù–ö–ê: –ö–µ—à–±—ç–∫ –¥–æ 30%! –ö–ª–∏–µ–Ω—Ç—ã –≤ —à–æ–∫–µ –æ—Ç —É—Å–ª–æ–≤–∏–π...',
      date: '15.12.2024',
      image: '/images/sberbank.jpg'
    },
    {
      id: 3,
      company: 'Wildberries',
      logoImage: '/images/logos/wildberries.png',
      category: '–ò–Ω—Ç–µ—Ä–Ω–µ—Ç-–º–∞–≥–∞–∑–∏–Ω—ã',
      description: '‚ö° –°–ö–ê–ù–î–ê–õ: –ë–ª–æ–≥–µ—Ä—ã —Ä–∞—Å–∫—Ä—ã–ª–∏ –ø—Ä–∞–≤–¥—É –æ —Ü–µ–Ω–∞—Ö –Ω–∞ Wildberries! –ß—Ç–æ —Å–∫—Ä—ã–≤–∞—é—Ç?',
      date: '14.12.2024',
      image: '/images/wildberries.jpg'
    },
    {
      id: 4,
      company: '–Ø–Ω–¥–µ–∫—Å',
      logoImage: '/images/logos/yandex.png',
      category: 'IT –∏ –∏–Ω—Ç–µ—Ä–Ω–µ—Ç',
      description: 'üö® –Ø–ù–î–ï–ö–° –¢–ê–ö–°–ò –ø–æ–¥–æ—Ä–æ–∂–∞–ª–æ –Ω–∞ 40%! –í–æ–¥–∏—Ç–µ–ª–∏ –∏ –ø–∞—Å—Å–∞–∂–∏—Ä—ã –≤ —è—Ä–æ—Å—Ç–∏...',
      date: '14.12.2024',
      image: '/images/yandex.jpg'
    },
    {
      id: 5,
      company: 'Ozon',
      logoImage: '/images/logos/ozon.png',
      category: '–ò–Ω—Ç–µ—Ä–Ω–µ—Ç-–º–∞–≥–∞–∑–∏–Ω—ã',
      description: 'üí∏ –û–ó–û–ù vs WILDBERRIES: –≠–ø–∏—á–Ω–∞—è —Ü–µ–Ω–æ–≤–∞—è –≤–æ–π–Ω–∞! –ö—Ç–æ –ø–æ–±–µ–¥–∏—Ç?',
      date: '13.12.2024',
      image: '/images/ozon.jpg'
    },
    {
      id: 6,
      company: '–¢–∏–Ω—å–∫–æ—Ñ—Ñ –ë–∞–Ω–∫',
      logoImage: '/images/logos/inkoff.png',
      category: '–ë–∞–Ω–∫–∏',
      description: 'üèÜ –¢–ò–ù–¨–ö–û–§–§ –û–ë–û–ì–ù–ê–õ –°–ë–ï–†–ë–ê–ù–ö –ø–æ –∫–ª–∏–µ–Ω—Ç—Å–∫–æ–º—É —Å–µ—Ä–≤–∏—Å—É! –ö–∞–∫ —Ç–∞–∫–æ–µ –≤–æ–∑–º–æ–∂–Ω–æ?',
      date: '13.12.2024',
      image: '/images/tinkoff.jpg'
    },
    {
      id: 7,
      company: '–ú–¢–°',
      logoImage: '/images/logos/mts.png',
      category: '–ú–æ–±–∏–ª—å–Ω–∞—è —Å–≤—è–∑—å',
      description: 'üì± –ú–¢–° –ó–ê–ü–£–°–¢–ò–õ 5G! –ù–æ –µ—Å—Ç—å –ø–æ–¥–≤–æ—Ö... –ê–±–æ–Ω–µ–Ω—Ç—ã –Ω–µ–≥–æ–¥—É—é—Ç!',
      date: '12.12.2024',
      image: '/images/mts.jpg'
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  return (
    <section className="slider-section">
      <div className="container">
        <div className="slider">
          <div className="slider-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
            {slides.map((slide, index) => (
              <div key={slide.id} className="slide">
                <div className="slide-content">
                  <div className="slide-image">
                    <img 
                      src={slide.logoImage} 
                      alt={`${slide.company} –ª–æ–≥–æ—Ç–∏–ø`}
                      className="company-logo-img"
                      onError={(e) => {
                        // Fallback –∫ —Ç–µ–∫—Å—Ç—É –ø—Ä–∏ –æ—à–∏–±–∫–µ –∑–∞–≥—Ä—É–∑–∫–∏
                        e.target.style.display = 'none';
                        e.target.nextElementSibling.style.display = 'block';
                      }}
                    />
                    <div className="company-logo" style={{display: 'none'}}>{slide.company}</div>
                  </div>
                  <div className="slide-info">
                    <div className="slide-date">{slide.date}</div>
                    <h3 className="slide-title">{slide.company}</h3>
                    <p className="slide-description">{slide.description}</p>
                    <span className="slide-category">{slide.category}</span>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <button className="slider-btn prev-btn" onClick={prevSlide}>‚Äπ</button>
          <button className="slider-btn next-btn" onClick={nextSlide}>‚Ä∫</button>

          <div className="slider-dots">
            {slides.map((_, index) => (
              <button
                key={index}
                className={`dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => goToSlide(index)}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Slider;
