        }
      ]
    },
    'apogej-ru': {
      name: 'Apogej.ru интернет-магазин отзывы',
      category: 'Автомобили',
      categorySlug: 'automobiles',
      date: '29.04.2025',
      reviews: [
        {
          id: 1,
          title: 'Отличное качество',
          author: 'Март',
          timeAgo: '17 февраля, 2025 год',
          content: 'Заказывал двигатель, но фото эндоскопии мне не показали. Сказали только, что движок в отличном состоянии...'
        }
      ]
    }
    // Добавить данные для других компаний по мере необходимости
  };

  const company = companiesData[slug] || {
    name: 'Компания не найдена',
    category: 'Неизвестно',
    categorySlug: 'unknown',
    date: new Date().toLocaleDateString(),
    reviews: []
  };

  return (
    <div className="company-page">
      <Header />
      <Breadcrumbs company={company} />

      <main className="company-main">
        <div className="container">
          <div className="company-header">
            <div className="company-category-badge">{company.category}</div>
            <h1 className="company-title">{company.name}</h1>
            <div className="company-date">{company.date}</div>
            {company.image && (
              <div className="company-image">
                <img src={company.image} alt={company.name} />
              </div>
            )}
          </div>

          <div className="company-content">
            <div className="company-reviews-section">
              <CompanyReviews reviews={company.reviews} />
            </div>
            <div className="company-sidebar-section">
              <CompanySidebar />
            </div>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
};

export default CompanyPage;
